import React, { useEffect, useState } from 'react';
import "./App.css";
import axios from "axios";
import { TextField, Button, Dialog, DialogContent, Typography } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import Alert from '@mui/material/Alert';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { response } from 'express';

const App = () => {
	const [products, setProducts] = useState([]);
	const [produitInfo, setProduitInfo] = useState({
		numProduit: '',
		design: '',
		prix: '',
		quantite: ''
	})
	const [addMod, setAddMod] = useState(false);
	const [showAlert, setShowAlert] = useState(false);
	const [confirmDel, setConfirmDel] = useState(false);
	const [idDel, setIdDel] = useState('')
	const [editMod, setEditMod] = useState(false);
	const [searchText, setSearchText] = useState('');
	const [idMod, setIdMod] = useState('')
	const [selectedProduct, setSelectedProduct] = useState(null);
	const [filteredData, setFilteredData] = useState('');
	const [produitEditedInfo, setProduitEditedInfo] = useState({
		numProduit: '',
		design: '',
		prix: '',
		quantite: ''
	});

	const handleChange = (event) => {
		const { name, value } = event.target;
		setProduitInfo((prevProducts) => ({
			...prevProducts,
			[name]: value,
		}));
	};

	const handleEditChange = (event) => {
		const { name, value } = event.target;
		setProduitEditedInfo((prevProduct) => ({
			...prevProduct,
			[name]: value,
		}));
		console.log(produitEditedInfo);
	}

	const fetchProducts = async () => {
		try {
			const response = await axios.get('http://localhost:3000/produits/');
			setProducts(response.data);
		} catch (error) {
			console.error('Error fetching products:', error);
		}
	};

	useEffect(() => {
		fetchProducts();
	}, []);

	const insertProducts = async () => {
		const response = await axios.post("http://localhost:3000/produits", produitInfo);
		console.log(produitInfo);
		fetchProducts()
		setAddMod(false)
		setShowAlert(true)
		setProduitInfo('')
		setTimeout(() => {
			setShowAlert(false);
		}, 3000);
	}

	const deleteProducts = async (numProduit) => {
		const response = await axios.delete(`http://localhost:3000/produits/${numProduit}`);
		fetchProducts()
		setShowAlert(true)
		setTimeout(() => {
			setShowAlert(false);
		}, 3000);
	}

	const handleSelectProduct = (product) => {
		setSelectedProduct(product);
	};

	const editProducts = async () => {
		try {
			const response = await axios.put(`http://localhost:3000/produits/${idMod}`, produitEditedInfo);
			console.log("RESPONSE" + response.data);
			fetchProducts();
			setEditMod(false);
			setShowAlert(true);
			setTimeout(() => {
				setShowAlert(false);
			}, 3000);
		} catch (error) {
			console.error('Error editing product:', error);
		}
	};

	const handleSearch = (searchText) => {
		const filteredData = response.data.filter((row) =>
			row.numProduit.includes(searchText) || row.design.includes(searchText) || row.prix.toString().includes(searchText)
		);
		setFilteredData(filteredData);
	}

	const handleInputChange = (e) => {
		const value = e.target.value;
		setSearchText(value);
		handleSearch(value);
	}

	return (
		<>
			{showAlert && (
				<Alert variant="filled" severity="success">
					Une Action à terminé avec succès !
				</Alert>
			)}
			<div >
				<h1 style={{ textAlign: 'center' }}>GESTION PRODUITS</h1>

				<div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
					<Button style={{ margin: '15px' }} startIcon={<AddCircleIcon />} size='medium' variant='contained' onClick={() => { setAddMod(true) }}>
						<strong>Ajouter</strong>
					</Button>
					<TextField style={{ marginRight: '15px' }}
						variant='outlined'
						size="small"
						label='Search'
						InputProps={{
							endAdornment: <SearchIcon fontSize='small' />,
						}}
						value={searchText}
						onInput={handleInputChange}
					/>
				</div>

				<Dialog size="sx" open={addMod} onClose={() => setAddMod(false)}>

					<DialogContent style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '10px', justifyContent: 'space-around' }}>
						<h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Ajouter produit</h4>
						<TextField style={{ marginBottom: '15px' }} size='small' label="NumProd" type='text' name="numProduit" value={produitInfo.numProduit} onChange={handleChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Design" type='text' name="design" value={produitInfo.design} onChange={handleChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Prix" type='text' name="prix" value={produitInfo.prix} onChange={handleChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Quantite" type='text' name="quantite" value={produitInfo.quantite} onChange={handleChange} />
						<div>
							<Button style={{ margin: '10px' }} color='success' variant="contained" onClick={insertProducts}>
								Valider
							</Button>
							<Button style={{ margin: '10px' }} variant="outlined" color="error" onClick={() => setAddMod(false)}>
								Annuler
							</Button>
						</div>
					</DialogContent>
				</Dialog>

				<Dialog size="md" open={confirmDel} onClose={() => setConfirmDel(false)}>
					<DialogContent style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '10px', justifyContent: 'space-around' }}>
						<Typography>
							Voulez-vous vraiment supprimer cette enregistrement ?
						</Typography>
						<div>
							<Button style={{ margin: '10px' }} color="secondary" onClick={() => { deleteProducts(idDel); setConfirmDel(false) }}>
								Oui
							</Button>
							<Button style={{ margin: '10px' }} color="error" variant="outlined" size="small" onClick={() => setConfirmDel(false)}>
								Non
							</Button>
						</div>
					</DialogContent>
				</Dialog>

				<Dialog size="md" open={editMod} onClose={() => setEditMod(false)}>

					<DialogContent style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '10px', justifyContent: 'space-around' }}>
						<h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Modifier produit</h4>
						<TextField style={{ marginBottom: '15px' }} size='small' label="Nouveau design" type='text' name='design' value={selectedProduct ? selectedProduct.design : ''} onChange={handleEditChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Nouveau prix" type='text' name='prix' value={selectedProduct ? selectedProduct.prix : ''} onChange={handleEditChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Nouveau quantite" type='text' name='quantite' value={selectedProduct ? selectedProduct.quantite : ''} onChange={handleEditChange} />
						<TextField style={{ marginBottom: '15px' }} size='small' label="Inchange numProduit" type='text' name='numProduit' value={selectedProduct ? selectedProduct.numProduit : ''} onChange={handleEditChange} />
						<div>
							<Button style={{ margin: '10px' }} color='success' variant="contained" onClick={editProducts}>
								Valider
							</Button>
							<Button style={{ margin: '10px' }} variant="outlined" color="error" onClick={() => setEditMod(false)}>
								Annuler
							</Button>
						</div>
					</DialogContent>
				</Dialog>
			</div>

			<div style={{ margin: '50px' }}>
				<TableContainer component={Paper} >
					<Table sx={{ minWidth: 700 }} aria-label="spanning table">
						<TableHead>
							<TableRow style={{ textAlign: 'center' }}>
								<TableCell style={{ fontWeight: 'bold' }}>Numéro produit</TableCell>
								<TableCell style={{ fontWeight: 'bold' }}>Designation</TableCell>
								<TableCell style={{ fontWeight: 'bold' }}>Prix</TableCell>
								<TableCell style={{ fontWeight: 'bold' }}>Quantité</TableCell>
								<TableCell style={{ fontWeight: 'bold' }}>Montant</TableCell>
								<TableCell style={{ fontWeight: 'bold' }}>Action</TableCell>
							</TableRow>
						</TableHead>
						<TableBody>
							{filteredData.map((row, index) => (
								<TableRow key={index}>
									<TableCell>{row.numProduit}</TableCell>
									<TableCell>{row.design}</TableCell>
									<TableCell>{(row.prix).toLocaleString('en-US', { minimumFractionDigits: 0 }) + " Ar"}</TableCell>
									<TableCell>{row.quantite}</TableCell>
									<TableCell>{(row.prix * row.quantite).toLocaleString('en-US', { minimumFractionDigits: 0 }) + " Ar"}</TableCell>
									<TableCell>
										<Button color="secondary" size="small" onClick={() => { setEditMod(true); handleSelectProduct(row) }}>Editer</Button>
										<Button variant="outlined" color="error" size="small" onClick={() => { setConfirmDel(true); setIdDel(row.numProduit) }}>Supprimer</Button>
									</TableCell>
								</TableRow>
							))}

							<TableRow>
								<TableCell rowSpan={5} />
								<TableCell colSpan={3} style={{ fontWeight: 'bold' }} align='center'>Prix minimal</TableCell>
								<TableCell align="left">
									{Math.min(...products.map((element) => element.prix)).toLocaleString('en-US', { minimumFractionDigits: 0 }) + " Ar"}
								</TableCell>
							</TableRow>
							<TableRow>
								<TableCell colSpan={3} style={{ fontWeight: 'bold' }} align='center'>Prix maximal</TableCell>
								<TableCell align="left">{Math.max(...products.map((element) => element.prix)).toLocaleString('en-US', { minimumFractionDigits: 0 }) + " Ar"}</TableCell>
							</TableRow>
							<TableRow>
								<TableCell colSpan={3} style={{ fontWeight: 'bold' }} align='center'>Prix Total</TableCell>
								<TableCell align="left">
									{products.reduce((total, element) => total + element.prix * element.quantite, 0).toLocaleString('en-US', { minimumFractionDigits: 0 }) + " Ar"}
								</TableCell>
							</TableRow>
						</TableBody>
					</Table>
				</TableContainer>
			</div>
		</>
	);
};

export default App;
